from transformers import AutoTokenizer , AutoModelForSeq2SeqLM
from peft import PeftModel 
model_path = 'EXt1/KMUTT-CPE35-thai-mt5base-summarizer'
base_model = AutoModelForSeq2SeqLM.from_pretrained("google/mt5-base")
# model = AutoModelForSeq2SeqLM.from_pretrained(model_path)
model = PeftModel.from_pretrained(base_model, model_path)
tokenizer = AutoTokenizer.from_pretrained(model_path)
def summarize_text(text, model, tokenizer, max_input_length=2048, max_output_length=1024):
    # ‡πÄ‡∏û‡∏¥‡πà‡∏° prompt ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏ä‡πà‡∏ß‡∏¢‡πÉ‡∏´‡πâ model ‡πÄ‡∏Ç‡πâ‡∏≤‡πÉ‡∏à task
    prompted_text = f"‡∏à‡∏±‡∏ö‡πÉ‡∏à‡∏Ñ‡∏ß‡∏≤‡∏°‡∏à‡∏≤‡∏Å‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ï‡πà‡∏≠‡πÑ‡∏õ‡∏ô‡∏µ‡πâ ‡πÇ‡∏î‡∏¢‡πÑ‡∏°‡πà‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏ô‡∏∑‡πâ‡∏≠‡∏´‡∏≤‡∏≠‡∏∑‡πà‡∏ô‡πÜ‡πÄ‡∏Ç‡πâ‡∏≤‡∏°‡∏≤ ‡∏™‡∏£‡∏∏‡∏õ‡πÉ‡∏´‡πâ‡∏Å‡∏£‡∏∞‡∏ä‡∏±‡∏ö‡∏Ç‡∏∂‡πâ‡∏ô: {text}"  # ‡∏´‡∏£‡∏∑‡∏≠‡πÉ‡∏ä‡πâ "‡∏™‡∏£‡∏∏‡∏õ: " ‡∏ñ‡πâ‡∏≤ fine-tune ‡∏î‡πâ‡∏ß‡∏¢‡∏†‡∏≤‡∏©‡∏≤‡πÑ‡∏ó‡∏¢‡∏•‡πâ‡∏ß‡∏ô
    
    # Tokenize input text
    inputs = tokenizer(prompted_text, return_tensors="pt", truncation=True, padding="max_length", max_length=max_input_length)
    
    # Move tensors to the same device as the model
    inputs = {key: val.to(model.device) for key, val in inputs.items()}

    summary_ids = model.generate(
        input_ids=inputs["input_ids"],
        attention_mask=inputs["attention_mask"],
        max_length=max_output_length,
        min_length=200,
        num_beams=10,
        do_sample=False,
        length_penalty=1.1,
        no_repeat_ngram_size=3,
        repetition_penalty=2.0,
        early_stopping=True
    )

    # Decode and return summary
    return tokenizer.decode(summary_ids[0], skip_special_tokens=True)
